function create_pagination() {
    let e = document.querySelectorAll(".pages"),
        t = document.getElementById("pagination__end__number"),
        n = document.querySelectorAll("#pageNumber");
    for (let i = 0; i < e.length; i++) {
        let a = document.createElement("div");
        (t.textContent = i >= 9 ? `${i + 1}` : `0${i + 1}`),
            (n[i].textContent = i >= 9 ? `${i + 1}` : `0${i + 1}`),
            0 == i ? (a.className = "pagination__cont__items pagination__cont__items--active") : (a.className = "pagination__cont__items"),
            document.querySelector(".pagination__cont").appendChild(a);
    }
}
function mobileBtn() {
    document.querySelectorAll("#menuBtnMobile").forEach((e, t) => {
        e.addEventListener("click", () => {
            document.querySelectorAll(".mibileNavigation")[t].classList.toggle("mibileNavigation--active");
        });
    });
};


(function () {
    create_pagination();
    let e = document.getElementById("btn__next"),
        t = document.getElementById("btn__prev"),
        n = document.querySelectorAll(".pages"),
        i = document.querySelectorAll(".pagination__cont__items"),
        a = document.getElementById("pagination__first__number"),
        d = document.querySelector('body'),
        o = 0,
        l = (e) => {
            let t;
            for (t of n) t.classList.remove("pages--active"), t.classList.remove("pages__active__animTop__To__Bottom");
            n[e].classList.add("pages--active"),
                n[e].classList.add("pages__active__animTop__To__Bottom"),
                (document.querySelector(".container").style.overflow = "hidden"),
                setTimeout(() => {
                    document.querySelector(".container").style.overflow = "inherit";
                }, 400);
        },
        c = (e) => {
            let t;
            for (t of i) t.classList.remove("pagination__cont__items--active");
            i[e].classList.add("pagination__cont__items--active");
        },
        s = () => {
            o == n.length - 1 ? g((o = 0)) : g(++o), (a.textContent = o >= 9 ? `${o + 1}` : `0${o + 1}`);
        },
        r = () => {
            0 == o ? g((o = n.length - 1)) : g(--o), (a.textContent = o >= 9 ? `${o + 1}` : `0${o + 1}`);
        },
        g = (e) => {
            l(e), c(e);
        };
    i.forEach((e, t) => {
        e.addEventListener("click", () => {
            g((o = t));
        });
    }),
        e.addEventListener("click", s),
        t.addEventListener("click", r);

    const scrollResize = () => {
        console.log(d.offsetWidth,d.offsetHeight);

        if (d.offsetHeight > 1200 && d.offsetWidth > 1005) {
            window.addEventListener('wheel', (e) => {
                (e.deltaY > 0) ? s() : r()
            })
        }
    }
    scrollResize()
    window.addEventListener("resize", scrollResize);

})(),
    mobileBtn();
